import React from "https://esm.sh/react@18.2.0";
import { ImageResponse } from 'https://deno.land/x/og_edge/mod.ts'

const font = fetch("https://label-studio-website-preview.netlify.app/fonts/SpaceGrotesk-Bold.ttf")
.then(
  (res) => res.arrayBuffer(),
);

const openSourceBackground = `url('data:image/png;base64,')`

const enterpriseBackground = `url('data:image/png;base64,')`

export default async function handler(req: Request) {
  const { searchParams } = new URL(req.url);
  const title = searchParams.get("title") || "";
  let category = searchParams.get("category") || "Docs";
  category = category === "guide" ? "docs" : category;

  const theme = req.url.includes("humansignal.com") ? "enterprise" : "opensource"

  const titleLength = title.length
  
  const fontData = await font;

    return new ImageResponse(
    (
      <div
        style={{
          width: '1200px',
          height: '630px',
          display: 'flex',
          paddingTop: '115px',
          paddingLeft: '74px',
          background: theme === "enterprise" ? enterpriseBackground : openSourceBackground
        }}
      >
        <h1
          style={{
          fontSize: titleLength > 33 ? '80px' : '100px',
          fontFamily: "SpaceGrotesk",
          maxWidth: '759px',
          lineHeight: titleLength > 33 ? '90px' : '110px',
          color: theme === "enterprise" ? "#fff" : "#131522" 
        }}>
          {title && decodeURI(title)}
        </h1>

        <h2
          style={{
          fontSize: '39px',
          fontFamily: "SpaceGrotesk",
          color: theme === "enterprise" ? "#FDB515" : "#FF8267",
          position: 'absolute',
          top: '510px',
          left: theme === "enterprise" ? '580px' : '375px',
          textTransform: 'capitalize'
        }}>
          {category && decodeURI(category)}
        </h2>
      </div>
    ),
    {
      fonts: [
        {
          name: "SpaceGrotesk",
          data: fontData,
          style: "normal",
        },
      ],
    }
  )
}